import React, { Component } from "react";
import axios from "axios";

class ProductDetailComponent extends Component {
  constructor() {
    super();
    this.state = {
      data: null
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:3001/products`).then(res => {
      const data = res.data;
      this.setState({ ...this.state, data });
    });
  }

  getProduct = (key, title) => {
    return this.state.data.filter(product => {
      console.log(product)
      product[key] == title;
    });
  };

  render() {
    let product;
    if (this.state.data && this.props.match.params.id) {
      product = this.getProduct("title", this.props.match.params.id);
    } else {
      product = this.state.data;
    }
    return (
      <div>
        <h1>
          {product.map(res => {
            return <h1>{res.title}</h1>;
          })}
        </h1>
      </div>
    );
  }
}

export default ProductDetailComponent;
